import { createApp } from 'vue'
import App from './App.vue'
import $ from "jquery"

createApp(App).mount('#app')

console.log("main");

$(".productImage").css("object-fit", "contain");
start();
// 偵測視窗大小
function start() {
    $(".productImage").css("height", $(".productImage").css("width"));
}
window.addEventListener("resize", start);
